<?php//// include the libraryrequire_once __DIR__ . '/vendor/autoload.php';use PhpAmqpLib\Connection\AMQPStreamConnection;// matches up with the queue that send publishes to.$connection = new AMQPStreamConnection('localhost', 5672, 'guest', 'guest');$channel = $connection->channel();$channel->queue_declare('test', false, false, false, false);//echo ' [*] Waiting for messages. To exit press CTRL+C', "\n" ;//that messages are sent$callback = function($msg) {    echo $msg->body;    echo "\n";};$channel->basic_consume('test', '', false, true, false, false, $callback);while(count($channel->callbacks)) {    $channel->wait();}//we close the channel and the connection$channel->close();$connection->close();