<?php// include the libraryrequire_once __DIR__ . '/vendor/autoload.php';use PhpAmqpLib\Connection\AMQPStreamConnection;use PhpAmqpLib\Message\AMQPMessage;//create a connection to the server$connection = new AMQPStreamConnection('localhost', 5672, 'guest', 'guest');$channel = $connection->channel();//publish a message to the queue:$channel->queue_declare('test', false, false, false, false);$msg = new AMQPMessage('{infogebana_togo:{\'pr_producer\'[{\'firstname\':\'Oliver\',\'PK_pr_producer\':\'AB_00012\'}]}}');$channel->basic_publish($msg, '', 'test');echo " Your message is = {infogebana_togo:{'pr_producer'[{'firstname':'Oliver','PK_pr_producer':'AB_00012'}]}}'\n";echo "";//we close the channel and the connection$channel->close();$connection->close();